mapper.select:
SELECT
*
FROM apply_loan_information




    order by ${sortord}


    limit #{offset},#{rows}

SELECT
FROM apply_loan_information a LEFT JOIN (SELECT flowNode,flowNodeStatus,flowNodeTime,flowHandlePerson,flowStatus,applyFlowId "aflowid"FROM apply_flow_status_information ORDER BY flowNodeTime DESC LIMIT 1) b ON a.id=b.aflowid
order by ${sortord}
limit #{offset},#{rows}

select loan.loanProjectName,loan.requestLimit,loan.hopeRate,loan.startTime,`user`.customerName,opt.* from apply_loan_information loan join rate_result_opinion opt on opt.loanId = loan.id join customer_user user on `user`.id = loan.customerUserId
1=1
and `user`.businessManId in (${userIds})
and `user`.customerName like concat('%',#{customerName},'%')
order by ${sortord}
limit #{offset},#{rows}

select loan.requestLimit,loan.hopeRate,loan.startTime,`user`.customerName,opt.*,company.loanCompanyName from rate_result_opinion opt left join apply_loan_information loan on opt.loanId = loan.id left join customer_user user on loan.customerUserId =`user`.id left join loan_company_information company on loan.id = company.applyFlowId
1=1
and loan.customerUserId = #{customerUserId}
ORDER BY loan.startTime desc
limit #{offset},#{rows}
